-- customer name and order total
select c.name 'Customer Name', sum(o.total) 'Order Total'
from orders o
join customer c
	on o.customerID = c.id
group by customerID
order by c.name;

-- count function
select count(*)
from orders;

-- max, min
select max(total) from orders;
select min(total) from orders;

-- select a row by id
select * from customer
where id = 5;

-- insert a new customer 
Insert into customer (name, city, state, isCorpAcct, creditLimit)
values ('Dummy ', 'dummy', 'DY', 0, 5000);

select * from customer;
-- another way
insert into customer (name, city, state, isCorpAcct, creditLimit)
values (9, 'Dummy 2', 'Dummy', 'DY', 0, 55550, 1);

-- 3rd way to insert...multiple rows
insert into customer (name, city, state, isCorpAcct, creditLimit)
values ('Dummy 3', 'Dummy', 'DY', 0, 5555.55),
	   ('Dummy 4', 'Dummy', 'DY', 0, 5555.55),
       ('Dummy 5', 'Dummy', 'DY', 0, 5555.55);
       
-- update statement
update customer
	set name = 'Dummy1'
    where id = 8;
    
select * from customer;    
-- delete 3 records
delete
from customer
where id in (11,12,14); 

delete from customer
where id > 7;

-- get the sum of all orders by customer
select customerId, sum(total) as OrderTotal
from orders
group by customerId
having OrderTotal > 1000;
